def _infer_clip_model_from_embedding_dim(embedding_dim: Optional[int], *, active_name: Optional[str] = None) -> Optional[str]:
    try:
        emb = int(embedding_dim or 0)
    except Exception:
        return None
    if emb == 768:
        return "ViT-L/14"
    if emb == 512:
        active = str(active_name or "").strip()
        if active in {"ViT-B/32", "ViT-B/16"}:
            return active
        return "ViT-B/32"
    return None

